      <AppContainer>
        <MessagesContainer/>
        <InputsContainer>
          <NameInput placeholder={'Nome'}/>
          <MessagemInput placeholder={'Mensagem'}/>
          <buttom>Enviar</buttom>
        </InputsContainer>  
      </AppContainer>  

    )

  }

}



export default App;
